package java_programs_te;

public class Continue21 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("even number using continue");
		

		for(int i=0;i<=20;i++){
			if(i%2!=0)
				continue;
			System.out.println(i);
		
		}

	}

}
