package java_programs_te;

public class Unaryopr10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
            int a=4;
         
            System.out.println("a durring post inc: "+a++);
            System.out.println("a after post inc: "+a);
            System.out.println("a durring post dec: "+a--);
            System.out.println("a after post dec: "+a);
            System.out.println("a durring pre inc: "+ ++a);
            System.out.println("a during pre dec: "+ --a);
            
	}

}
